import React, { useState } from 'react';
import axios from 'axios';
import { StyleSheet, Text, View, TextInput } from 'react-native';



export default function App() {
  const apiurl = "https://api.themoviedb.org/3/movie/movie?api_key=d24c52c68333a633aa24bd3c71091439"
  const [state, setState] = useState({
    s: "Enter a movie...",
    results: [],
    selected: {}
  });

  const search = () => {
    axios(apiurl + "&query=" + state.s).then(({ data }) => {
      let results = data.Search
      console.log(results)
      setState(prevState => {
        return { ...prevState, results: results }
      })
    })
  }

  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app Dude MAAAN!</Text>
      <TextInput 
        style={styles.searchBox} 
        onChangeText={text => setState(prevState => { return {...prevState, s: text}})}
        value={state.s}
        />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  searchBox: {
    fontSize: 20,
    fontWeight: '300',
    padding: 20,
    width: '100%',
    borderRadius: 8,
    marginBottom: 40
  }
});
